import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { decreaseCount, deleteOptions, increaseCount } from "../modules/item";

const ItemDetail = ({ i, item }) => {
  const { content, price } = item;
  const counts = useSelector((state) => state.counts);
  const dispatch = useDispatch();
  return (
    <div className="item-option">
      <div className="option-name">
        <span>{content}</span>
        <button
          onClick={() => {
            dispatch(deleteOptions({ i, item }));
          }}
        >
          <img
            src="data:image/svg+xml;base64,"
            alt=""
          />
        </button>
      </div>
      <div className="option">
        <div className="option-count">
          <button
            onClick={() => {
              dispatch(decreaseCount({i, item}));
            }}
          ></button>
          <div>{counts[i]}</div>
          <button
            onClick={() => {
              dispatch(increaseCount({i, item}));
            }}
          ></button>
        </div>
        <div className="option-price">
          <span>{price}</span>
        </div>
      </div>
    </div>
  );
};

export default ItemDetail;
